      }
      .carousel-item .info h4 {
        padding-top: 5px;
        cursor: pointer;
      }
      .stars {
        display: flex;
        align-items: center;
        justify-content: flex-start;
        padding: 24px 0;
      }
      .carousel-item .info .stars {
        margin-top: -20px;
      }
      .carousel-item .info .stars i {
        color: $secondary-dark;
        padding: 10px 0;
      }
      .carousel-item ~ .carousel-item {
        transform: translate3d(20px, 0, 0);
      }
    }
    .carousel__container:hover .carousel-item:hover {
      transform: scale(1.09);
      opacity: 1;
    }
  }

  .carousel::-webkit-scrollbar {
    height: 5px;
  }
  .carousel::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.26);
    border-radius: 10px;
  }
  .carousel::-webkit-scrollbar-track-piece {
    max-width: 100px;
  }
  .carousel::-webkit-scrollbar-thumb {
    border-radius: $standard-br;
    background-color: #fff;
    background-image: -webkit-gradient(
      linear,
      10% 0%,
      75% 84%,
      from($primary-dark),
      to($secondary-light),
      color-stop(0.6, $primary-light)
    );
  }
}

